mapper.select:
select * from user_tb user1,role r
 where user1.role_id=r.roleid
  and user1.user_name=#{username}

select * from user_tb u,user_class_major ucm,
role r where u.user_id=ucm.userid and u.role_id=r.roleid

  and ucm.departid=#{did}


  and ucm.majorid=#{mid}


  and  r.rolename=#{rolename}
